import React from 'react'
import Paper from '@mui/material/Paper';
import { useEffect, useState } from "react";

export default function SavedConfigurations() {
const paperstyle ={padding:"100px 50px", width:400, margin:"20px auto"}
const paperstyle2 ={padding:"10px 50px", width:150, margin:"20px auto"}

const [data, setData] = useState([]);

    useEffect(() => {
        const socket = new WebSocket("ws://localhost:8080/api/configurations/list");

        socket.onmessage = (event) => {
            const newData = JSON.parse(event.data);
            setData(newData);
        };

        socket.onerror = (error) => {
            console.error("WebSocket error:", error);
        };
        return () => socket.close(); // Cleanup on unmount
    }, []);

  return (
    <div style={{position:"absolute", top:"80px", left:"800px"}}>
         <Paper elevation={3} style={paperstyle}>
         <h4><u>SavedConfigurations</u></h4>
            <Paper elevation={2} style={paperstyle2}>
            <ul>
                {data.map((config, index) => (
                    <li key={index}>{config.eventName}</li>
                ))}
            </ul>
            </Paper>
         </Paper>
    </div>
  )
}
