import React, { useState, useEffect } from 'react';
import SockJS from 'sockjs-client';
import Stomp from 'stompjs';
import { Paper } from "@mui/material";

const ConfigurationLoader = () => {
  const [fileName, setFileName] = useState('');
  const [configuration, setConfiguration] = useState(null);
  const [error, setError] = useState('');
  const [progress, setProgress] = useState('');
  const [isPaused, setIsPaused] = useState(false);
  const [stompClient, setStompClient] = useState(null);

  const paperStyle = { padding: "100px 50px", width: 600, margin: "20px auto" };
  const paperStyle2 = { padding: "10px 50px", width: 350, margin: "20px auto" };

  const handleInputChange = (event) => {
    setFileName(event.target.value);
  };

  const loadConfiguration = async () => {
    if (!fileName) {
      setError('Please enter a file name.');
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/api/configurations/load?fileName=${fileName}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Failed to load configuration');
      }

      const data = await response.json();
      setConfiguration(data);
      setError('');
    } catch (err) {
      setError(`Error: ${err.message}`);
    }
  };

  const startSimulation = () => {
    if (!configuration) {
      setError('Please load a configuration first.');
      return;
    }

    const socket = new SockJS('http://localhost:8080/simulation'); // WebSocket endpoint
    const stomp = Stomp.over(socket);

    stomp.connect({}, () => {
      stomp.subscribe('/topic/progress', (message) => {
        setProgress((prevProgress) => prevProgress + '\n' + message.body); // Append progress
      });
    });

    setStompClient(stomp);
    setError('');
  };

  const pauseSimulation = async () => {
    await fetch('http://localhost:8080/api/simulation/pause', { method: 'POST' });
    setIsPaused(true);
  };

  const resumeSimulation = async () => {
    await fetch('http://localhost:8080/api/simulation/resume', { method: 'POST' });
    setIsPaused(false);
  };

  useEffect(() => {
    return () => {
      if (stompClient) stompClient.disconnect(); // Cleanup WebSocket connection
    };
  }, [stompClient]);

  return (
    <div>
      <Paper elevation={3} style={paperStyle}>
        <h3>Select Configuration</h3>
        <h5>and load</h5>
        <br></br>
        <input
          type="text"
          value={fileName}
          onChange={handleInputChange}
          placeholder="Enter JSON file name"
        />
        <button onClick={loadConfiguration}>Load</button>
        <Paper elevation={3} style={paperStyle2}>
          {error && <p style={{ color: 'red' }}>{error}</p>}

          {configuration && (
            <div>
              <h4>Configuration Data:</h4>
              <pre>{JSON.stringify(configuration, null, 2)}</pre>
            </div>
          )}
        </Paper>
        <button onClick={startSimulation} disabled={!configuration}>
          Start Simulation
        </button>
        <h3>Simulation Progress</h3>
        <pre>{progress}</pre> {/* Display progress */}
        <button onClick={pauseSimulation} disabled={isPaused}>Pause</button>
        <button onClick={resumeSimulation} disabled={!isPaused}>Resume</button>
      </Paper>
    </div>
  );
};

export default ConfigurationLoader;
