import React, { useState, useEffect } from "react";
import { Paper } from "@mui/material";
import axios from "axios";
import { Client } from "@stomp/stompjs";

const ConfigurationLoaderAndSimulator = () => {
  const [fileName, setFileName] = useState("");
  const [configuration, setConfiguration] = useState(null);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [realTimeUpdates, setRealTimeUpdates] = useState([]);
  const paperstyle = { padding: "100px 50px", width: 600, margin: "20px auto" };
  const paperstyle2 = { padding: "10px 50px", width: 350, margin: "20px auto" };
  const paperstyle3 = { padding: "10px 50px", width: 450, margin: "20px auto" };

  useEffect(() => {
    // Initialize WebSocket client
    const client = new Client({
      brokerURL: "ws://localhost:8080/ws", // Corrected WebSocket endpoint
      reconnectDelay: 5000, // Reconnect every 5 seconds if disconnected
      onConnect: () => {
        console.log("WebSocket connected");
  
        // Subscribe to /topic/updates
        client.subscribe("/topic/updates", (message) => {
          const newUpdate = message.body; // The message is a plain string
          setRealTimeUpdates((prev) => [...prev, newUpdate]);
        });
  
        // Subscribe to /topic/progress
        client.subscribe("/topic/progress", (message) => {
          const newProgress = message.body; // The message is a plain string
          setRealTimeUpdates((prev) => [...prev, newProgress]);
        });
      },
      onDisconnect: () => console.log("WebSocket disconnected"),
    });
  
    client.activate();
  
    return () => {
      client.deactivate();
    };
  }, []);
  

  const handleInputChange = (event) => {
    setFileName(event.target.value);
  };

  const loadConfiguration = async () => {
    if (!fileName) {
      setError("Please enter a file name.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/api/configurations/load?fileName=${fileName}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error("Failed to load configuration");
      }

      const data = await response.json();
      setConfiguration(data);
      setError("");
    } catch (err) {
      setError(`Error: ${err.message}`);
    }
  };

  const startSimulation = async () => {
    if (!configuration) {
      setError("Please load a configuration first.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:8080/api/threadController/threadRunning");
      setMessage(response.data);
    } catch (error) {
      console.error("Error starting simulation:", error);
      setMessage("Failed to start simulation.");
    }
  };

  return (
    <div>
      <Paper elevation={3} style={paperstyle}>
        <h3>Select Configuration</h3>
        <h5>and load</h5>
        <br></br>
        <input
          type="text"
          value={fileName}
          onChange={handleInputChange}
          placeholder="Enter JSON file name"
        />
        <button onClick={loadConfiguration}>Load</button>
        <Paper elevation={3} style={paperstyle2}>
          {error && <p style={{ color: "red" }}>{error}</p>}

          {configuration && (
            <div>
              <h4>Configuration Data:</h4>
              <pre>{JSON.stringify(configuration, null, 2)}</pre>
            </div>
          )}
        </Paper>
        <button onClick={startSimulation}>Start Simulation</button>
        <Paper elevation={3} style={paperstyle3}>
          {message && <p>{message}</p>}
          <h4>Real-Time Updates</h4>
          <ul>
            {realTimeUpdates.map((update, index) => (
              <li key={index}>{update}</li>
            ))}
          </ul>
        </Paper>
        <button style={{ backgroundColor: "yellow", width: "30px", margin: "20px" }}></button>
        <button style={{ backgroundColor: "red", width: "30px" }}></button>
      </Paper>
    </div>
  );
};

export default ConfigurationLoaderAndSimulator;
